import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

interface Post {
    id: number;
    title: string;
    content: string;
    author: string;
    view_count: number;
    formatted_date: string;
}

const PostDetail: React.FC = () => {
    const [post, setPost] = useState<Post | null>(null);
    const [viewCount, setViewCount] = useState<number>(0);
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const currentUser = localStorage.getItem("username");  // 현재 로그인한 사용자 조회

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/posts/${id}`);
                if (response.ok) {
                    const data = await response.json();
                    setPost(data);
                    setViewCount(data.viewCount); // viewCount만 업데이트
                } else {
                    navigate("/notfound", { replace: true });
                }
            } catch (error) {
                console.error("게시글 불러오기 에러:", error);
                navigate("/notfound", { replace: true });
            }
        };

        fetchPost();


        fetch(`http://localhost:5001/api/posts/${id}/view`, {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
        })
            .then(response => response.json())
            .then(data => {
                if (data.viewCount !== undefined) {
                    setViewCount(data.viewCount);
                }
            })
            .catch(error => console.error("조회수 증가 에러:", error));
    }, [id, navigate]);



    const handleDelete = async () => {
        if (!window.confirm("정말 삭제하시겠습니까?")) return;

        try {
            const response = await fetch(`http://localhost:5001/api/posts/${id}`, {
                method: "DELETE",
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });

            if (response.ok) {
                navigate("/posts"); // 삭제 후 목록 페이지로 이동
            } else {
                alert("게시글 삭제 실패!");
            }
        } catch (error) {
            console.error("게시글 삭제 에러:", error);
        }
    };


    if (!post) return <div>로딩 중...</div>;

    return (
        <div className="table-wrapper">
            <h2>게시글 상세</h2>
            <table className="post-table">
                <tbody>
                    <tr>
                        <th>제목</th>
                        <td>{post.title}</td>
                    </tr>
                    <tr>
                        <th>작성자</th>
                        <td>{post.author}</td>
                    </tr>
                    <tr>
                        <th>조회수</th>
                        <td>{post.view_count}</td>
                    </tr>
                    <tr>
                        <th>내용</th>
                        <td>{post.content}</td>
                    </tr>
                    <tr>
                        <th>작성일자</th>
                        <td>{post.formatted_date}</td>
                    </tr>
                </tbody>
            </table>

            <div className="button-container">
                <button className="secondary" onClick={() => navigate("/posts")}>목록으로</button>

                {currentUser === post.author && (
                    <>
                        <button onClick={() => navigate(`/edit/${id}`, { state: { title: post.title, content: post.content } })}>
                            수정
                        </button>
                        <button className="secondary" onClick={handleDelete}>
                            삭제
                        </button>
                    </>
                )}
            </div>
        </div>
    );
};

export default PostDetail;
