import type React from "react"
import { useState } from "react"
import { useNavigate } from "react-router-dom"

const CreatePost: React.FC = () => {
    const [title, setTitle] = useState("")
    const [content, setContent] = useState("")
    const navigate = useNavigate()

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault()
        try {
            const response = await fetch("http://localhost:5001/api/posts", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify({ title, content }),
            })
            if (response.ok) {
                navigate("/posts")
            }
        } catch (error) {
            console.error("게시글 작성 에러:", error)
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>새 게시글 작성</h2>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="제목" required />
            <textarea value={content} onChange={(e) => setContent(e.target.value)} placeholder="내용" required />
            <button type="submit">게시글 작성</button>
            <button type="button" className="secondary" onClick={() => navigate(-1)}>뒤로 가기</button>
        </form>
    )
}

export default CreatePost
