import type React from "react"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

interface Post {
    id: number
    title: string
    content: string
    author: string
}

const PostList: React.FC = () => {
    const [posts, setPosts] = useState<Post[]>([])

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch("http://localhost:5001/api/posts", {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("token")}`,
                    },
                })
                if (response.ok) {
                    const data = await response.json()
                    setPosts(data)
                }
            } catch (error) {
                console.error("게시글 불러오기 에러:", error)
            }
        }

        fetchPosts()
    }, [])

    return (
        <div>
            <h2 className="text-2xl font-bold mb-4">게시글 목록</h2>
            <ul className="space-y-2">
                {posts.map((post) => (
                    <li key={post.id} className="border p-2 rounded">
                        <Link to={`/post/${post.id}`} className="text-blue-500 hover:underline">
                            {post.title}
                        </Link>
                        <span className="ml-2 text-gray-500">- {post.author}</span>
                    </li>
                ))}
            </ul>
            <Link to="/create-post" className="mt-4 inline-block p-2 bg-green-500 text-white rounded">
                새 게시글 작성
            </Link>
        </div>
    )
}

export default PostList
