import type React from "react";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

interface Post {
    id: number;
    title: string;
    content: string;
    author: string;
    viewCount: number;
}

const PostDetail: React.FC = () => {
    const [post, setPost] = useState<Post | null>(null);
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const currentUser = localStorage.getItem("username"); // 

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/posts/${id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem("token")}`,
                    },
                });
                if (response.ok) {
                    const data = await response.json();
                    setPost(data);
                } else {
                    navigate("/notfound", { replace: true }); // 존재하지 않는 게시글이면 /notfound로 이동
                }
            } catch (error) {
                console.error("게시글 불러오기 에러:", error);
                navigate("/notfound", { replace: true }); // 오류 발생 시 /notfound로 이동
            }
        };

        fetchPost();
    }, [id, navigate]);

    const handleDelete = async () => {
        if (!window.confirm("정말 삭제하시겠습니까?")) return;

        try {
            const response = await fetch(`http://localhost:5001/api/posts/${id}`, {
                method: "DELETE",
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            if (response.ok) {
                navigate("/posts");
            }
        } catch (error) {
            console.error("게시글 삭제 에러:", error);
        }
    };

    if (!post) return <div>로딩 중...</div>;

    return (
        <div className="post-detail-card">
            <h3>{post.title}</h3>
            <p className="author">작성자: {post.author}</p>
            <p className="view-count">조회수: {post.viewCount}</p>
            <p className="content">{post.content}</p>

            {currentUser === post.author && (
                <>
                    <button onClick={() => navigate(`/edit/${id}`, { state: { title: post.title, content: post.content } })}>
                        수정
                    </button>
                    <button className="secondary" onClick={handleDelete}>
                        삭제
                    </button>
                </>
            )}

            <button onClick={() => navigate("/posts")}>목록으로</button>
        </div>
    );
};

export default PostDetail;
